import { NDKUser, filterFromId } from "@nostr-dev-kit/ndk";
import { Skeleton, Typography, Image, Col, Layout, Row, theme, Button } from "antd";
import { UseNostrStoreType, NostrContext } from "app/contexts/NostrContext";
import { useContext } from "react";
import { useTranslation } from "react-i18next";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import { visit } from 'unist-util-visit';
import { nip19 } from 'nostr-tools'
import { useNavigate } from "react-router-dom";

const { Link, Text } = Typography;

interface MarkdownProps {
  text: string
  dTag?: string
  loadingAuthors?: boolean
}

export const Markdown: React.FC<MarkdownProps> = ({ text, dTag, loadingAuthors }) => {
  const {
    token: { colorBgLayout, colorTextSecondary, colorBgContainerDisabled },
  } = theme.useToken();
  const { authors, comments } = useContext<UseNostrStoreType>(NostrContext);
  const { t } = useTranslation()
  const navigate = useNavigate();
  const markdownComponent = {
    a: props => {
      const imageRegex = /\.(jpg|jpeg|png|gif|bmp|webp|svg)$/i
      return imageRegex.test(props.href) ?
        <Row justify="center">
          <Image
            src={props.href}
            fallback="data:image/png;base64,"
          />
        </Row>
        : <Link target="_blank" href={props.href}>{props.children}</Link>
    },
    img: props => {
      return <Row justify="center">
        <Image
          src={props.src}
          fallback="data:image/png;base64,"
        />
      </Row>
    },
    code: props => {
      return (
        <Col span={24}>
          <Row style={{ backgroundColor: colorBgContainerDisabled }} justify="center">
            <Col span={23}>
              <Text>{t('components.markDown.code')}</Text>
            </Col>
          </Row>
          <Row justify="center">
            <Layout
              style={{
                background: colorBgLayout,
                padding: 25,
                marginBottom: 10,
                overflowX: 'auto',
                width: 300
              }}
            >
              {props.children}
            </Layout>
          </Row>
        </Col>
      )
    },
  }

  const mentionComponent = (npub: string): JSX.Element => {
    const user = new NDKUser({ npub })
    const profile = authors?.[user.pubkey] ?? user?.profile
    const name: string = profile?.displayName ?? profile?.name ?? t("shared.events.anonymous")
    return loadingAuthors ?
      <Skeleton.Input size="small" active />
      : <Link strong href={`https://njump.me/${npub}`} target="_blank">{name}</Link>
  }

  const eventComponent = (nevent: string): JSX.Element => {
    let eventText
    if (dTag) {
      const eventId = filterFromId(nevent)?.['#e']?.[0]
      eventText = comments[dTag].find(c => c.id === eventId)
    }
    return (
      <Layout style={{ background: colorBgLayout, padding: 25, marginBottom: 10, borderLeftColor: colorTextSecondary, borderLeftWidth: 5, borderLeftStyle: 'solid' }}>
        <Row>
          <Col>
            <Skeleton active loading={!eventText}>
              {eventText}
            </Skeleton>
          </Col>
        </Row>
      </Layout>
    )
  }

  const noteComponent = (note1: string): JSX.Element => {
    return (
      <Layout style={{ background: colorBgLayout, padding: 25, marginBottom: 10, borderLeftColor: colorTextSecondary, borderLeftWidth: 5, borderLeftStyle: 'solid' }}>
        <Row>
          <Col>
            <Link strong href={`https://njump.me/${note1}`} target="_blank">{t('components.markDown.seeNote')}</Link>
          </Col>
        </Row>
      </Layout>
    )
  }

  const topicComponent = (naddr: string, identifier: string): JSX.Element => {
    if (!identifier || identifier === dTag) return <></>
    return (
      <Row>
        <Col>
          <Button type="link" onClick={() => navigate(`/topic/${naddr}`)}>
            {t('components.markDown.identifier', { identifier })}
          </Button>
        </Col>
      </Row>
    )
  }

  const applyComponent = (type: string, bech32: string): JSX.Element => {
    if (type === 'npub' || type === 'nprofile') {
      return mentionComponent(bech32)
    } else if (type === 'nevent') {
      return eventComponent(bech32)
    } else if (type === 'note1') {
      return noteComponent(bech32)
    } else if (type === 'naddr') {
      const ref = nip19.decode(bech32)
      // @ts-expect-error
      if (ref.data.kind === 30023) {
        // @ts-expect-error
        return topicComponent(bech32, ref.data.identifier)
      } else {
        return eventComponent(bech32)
      }
    } else {
      return <></>
    }
  }


  const nostrSyntax = (): (tree: any) => void => {
    return (tree) => {
      visit(tree, 'text', (node) => {
        const regex = /(?:nostr:)?((naddr|nevent|npub|nprofile|note1)[a-zA-Z0-9]+)/g
        const match = regex.exec(node.value)
        if (match) {
          node.type = 'root';
          node.children = []
          node.children.push({
            type: 'custom',
            value: applyComponent(match[2], match[1]),
          });
          node.children.push({
            type: 'text',
            value: node.value.replace(match[0], ""),
          });
        }
      });
    };
  };

  const parseHTML = (): string => {
    return text.replace(/<img\s+src="([^"]+)"\s*[^>]*>/g, (match, src) => {
      const altText = 'Image';
      return `![${altText}](${src})`;
    });
  }

  return (
    <ReactMarkdown remarkPlugins={[remarkGfm, nostrSyntax]} className={"markdown-container"} components={markdownComponent}>
      {parseHTML()}
    </ReactMarkdown>
  )
}
